mapper.update:

UPDATE member
SET deletedate = SYSDATE
WHERE member_id = #{memberId}


UPDATE member
SET password = #{password}
WHERE member_id = #{memberId}


update member
set selected_cnt = selected_cnt + 1
where member_id = #{memberId}


update member
set selected_cnt = selected_cnt - 1
where member_id = #{memberId}

mapper.select:

SELECT COUNT(username)
FROM member
WHERE username = #{username}
