mapper.select:

select submission_id, member_id, recommend_cnt, regdate, deletedate, content, sketch
from submission
where deletedate is null
  and relay_id=#{relay_id}
  and week=#{week}
order by recommend_cnt desc


select submission_id, member_id, recommend_cnt, regdate, deletedate, content, sketch
from submission
where deletedate is null
  and relay_id=#{relay_id}
  and week=#{week}
order by regdate desc

mapper.update:

UPDATE submission
SET deletedate = sysdate
WHERE submission_id = #{submissionId}

mapper.select:
SELECT * FROM submission
WHERE relay_id = #{relay_id}
  AND is_selected = #{is_selected}
