mapper.select:

select submission_id submissionId, member_id memberId, recommend_cnt recommendCnt, regdate, deletedate, content, TO_CHAR(sketch) sketch
from submission
where deletedate is null
  and relay_id=#{relayId}
  and week=#{week}
order by recommend_cnt desc


select submission_id submissionId, member_id memberId, recommend_cnt recommendCnt, regdate, deletedate, content, TO_CHAR(sketch) sketch
from submission
where deletedate is null
  and relay_id=#{relayId}
  and week=#{week}
order by regdate desc

mapper.update:

UPDATE submission
SET deletedate = sysdate
WHERE submission_id = #{submissionId}

mapper.select:
SELECT * FROM submission
WHERE relay_id = #{relayId}
  AND is_selected = #{isSelected}
