package com.doodling.reply.service;

import com.doodling.admin.dto.RelayInsertRequestDTO;
import com.doodling.admin.service.AdminRelayService;
import lombok.extern.slf4j.Slf4j;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.test.context.ContextConfiguration;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.test.context.web.WebAppConfiguration;

import java.util.Date;
import java.util.stream.IntStream;

@WebAppConfiguration // 모든 테스트에 어노테이션 추가
@RunWith(value = SpringJUnit4ClassRunner.class)
@ContextConfiguration(locations = "file:**/*-context.xml")
@Slf4j
public class RelayServiceTests {

  @Autowired
  public AdminRelayService adminRelayService;

  private final static String IMAGE = "data:image/png;base64,";
  private final static long CONTINUE_FOR = 1000 * 60 * 60 * 24 * 7;

  @Test
  public void registerRelayTest() {

    IntStream.range(0, 8).forEach(i -> {
      adminRelayService.insertRelay(
              RelayInsertRequestDTO.builder()
                      .title("title"+i)
                      .cover(IMAGE)
                      .age(i%2)
                      .startdate(new Date())
                      .enddate(new Date(new Date().getTime() + CONTINUE_FOR))
                      .build()
      );
    });
  }
}
